func removeNthFromEnd(head *ListNode, n int) *ListNode {
    // n + 1
    // 
    newHead := &ListNode{}
    newHead.Next = head
    p1 := newHead
    p2 := newHead
    for ; p1 != nil && n >= 0; {
        p1 = p1.Next
        n--
    }

    for ; p1 != nil; {
        p1 = p1.Next
        p2 = p2.Next
    }

    // p2.Next
    tmp := p2.Next
    p2.Next = p2.Next.Next
    tmp.Next = nil

    return newHead.Next
}
